			var goalTile = getIndex(goal,Tile)
			var d1 = (goalTile.y - i)**2
			var d2 = (goalTile.x - j)**2
			d += Math.sqrt(d1 + d2)

		}
	}
	return d
}

export default {
	misplaced: misplaced,
	manhattan: manhattan,
  };

